                    this.setState({
                        arrowShow:arrowTips as Array<boolean>,
                        tipsData: tips,
                        arrowDir:arrowDir
                    })
                }
                [45, 46, 47, 48, 49, 50, 51, 52, 53].forEach(i => {
                    temp[i][2] = temp[i][2] + 2
                });
                [24, 25, 26, 33, 34, 35, 42, 43, 44, 15, 16, 17].forEach(i => {
                    temp[i][1] = temp[i][1] + 2
                })
                break;

        }

        this.openAnimation()
        this.setState({
            positionData: temp,
        })
        setTimeout(() => {
            this.closeAnimation()
            if (this.state.openTips) {
                this._resetTips()
            }
            this._updateColor(value)
            this.resetPosition()
        }, this.state.stepTime * 800);
    }
    rotate = (value: string) => {
        let queue = value.split(' ')
        console.log(queue)
        let roll = () => {
            if (queue.length === 0) {
                return
            }
            this._rotate(queue.shift() as string)
            console.log(queue)
            setTimeout(() => {
                roll()
            }, 1000 * this.state.stepTime);
        }
        roll()
    }
